import React from 'react';
import { Link } from 'react-router-dom';

function Paginator({ totalPage, handleCurrentPage, currentPage, isActive, onNextClick, onPreviousClick }) {
  // const [listPages, setListPages] = React.useState([]);
  let listPages = [];

  for(let i = 1; i <= totalPage; i++) {
    listPages.push(i);
  };

  return (
    <nav aria-label="...">
      <ul className="pagination">
        <li className={isActive ? "page-item" : "page-item disabled"}>
          <Link className="page-link" to="#" tabIndex="-1" onClick={() => {onPreviousClick()}}>Previous</Link>
        </li>
        {
          listPages.map((page, index) => {
            return (
              <li className={!true ? "page-item" : "page-item active"} key={index}>
                <Link className="page-link" to="#" onClick={() => {handleCurrentPage(page)}}>
                  {page}
                  {/* <span className="sr-only">{currentPage}</span> */}
                </Link>
              </li>
            );
          })
        }
        {/* <li className="page-item">
          <a className="page-link" href="#">1</a>
        </li>
        <li className="page-item active">
          <a className="page-link" href="#">
            2 
            <span className="sr-only">(current)</span>
          </a>
        </li>
        <li className="page-item">
          <a className="page-link" href="#">3</a>
        </li> */}
        <li className="page-item">
          <Link className="page-link" to="#" onClick={() => {onNextClick()}}>Next</Link>
        </li>
      </ul>
    </nav>
  );
}

export default Paginator;
