import React from 'react';

function Paginator({ totalPage, handleCurrentPage, currentPage }) {
  // const [listPages, setListPages] = React.useState([]);
  let listPages = [];

  for(let i = 1; i <= totalPage; i++) {
    listPages.push(i);
  };

  return (
    <nav aria-label="...">
      <ul className="pagination">
        <li className="page-item disabled">
          <a className="page-link" href="#" tabIndex="-1">Previous</a>
        </li>
        {
          listPages.map((page, index) => {
            return (
              <li className="page-item" key={index}>
                <a className="page-link" href="#" onClick={() => {handleCurrentPage(page)}}>
                  {page}
                  {/* <span className="sr-only">{currentPage}</span> */}
                </a>
              </li>
            );
          })
        }
        {/* <li className="page-item">
          <a className="page-link" href="#">1</a>
