import React from 'react';
import DownIcon from '../utils/DownIcon';
import UpIcon from '../utils/UpIcon';

function Table({ listUsers, handleSortColunm, directionSort }) {

  return (
      <table className="table">
        <thead>
          <tr>
            <th onClick={() => {handleSortColunm('id')}}>
              id
              {directionSort ? <DownIcon /> : <UpIcon />}
            </th>
            <th onClick={() => {handleSortColunm('firstName')}}>
              firstName
              {directionSort ? <DownIcon /> : <UpIcon />}
            </th>
            <th onClick={() => {handleSortColunm('lastName')}}>
              lastName
              {directionSort ? <DownIcon /> : <UpIcon />}
            </th>
            <th onClick={() => {handleSortColunm('email')}}>
              email
              {directionSort ? <DownIcon /> : <UpIcon />}
            </th>
            <th onClick={() => {handleSortColunm('phone')}}>
              phone
            </th>
          </tr>
        </thead>
        <tbody>
          {listUsers.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.firstName}</td>
              <td>{item.lastName}</td>
              <td>{item.email}</td>
              <td>{item.phone}</td>
            </tr>
          ))}
        </tbody>
      </table>
  );
}

export default Table;
